import React from 'react';
import { HashLink as Link } from 'react-router-hash-link';

const Navigation = () => {
  const scrollWithOffset = (el) => {
    const yCoordinate = el.getBoundingClientRect().top + window.pageYOffset;
    const yOffset = -80; 
    window.scrollTo({ 
      top: yCoordinate + yOffset, 
      behavior: 'smooth' 
    }); 
  }

  return (
    <nav className="nav">
      <ul>
        <li><Link smooth to="#hakkimda" scroll={scrollWithOffset}>Hakkımda</Link></li>
        <li><Link smooth to="#deneyim" scroll={scrollWithOffset}>Deneyim</Link></li>
        <li><Link smooth to="#egitim" scroll={scrollWithOffset}>Eğitim</Link></li>
        <li><Link smooth to="#yetenekler" scroll={scrollWithOffset}>Yetenekler</Link></li>
        <li><Link smooth to="#referanslar" scroll={scrollWithOffset}>Referanslar</Link></li>
      </ul>
    </nav>
  );
};

export default Navigation;
