import React from 'react';
import { useLanguage } from '../context/LanguageContext';

const Experience = () => {
  const { language } = useLanguage();

  const content = {
    tr: {
      education: {
        title: 'Eğitim',
        items: [
          {
            school: 'Yaşar Üniversitesi',
            date: '2017 - 2022',
            degree: 'Yazılım Mühendisliği',
            description: '3.57 GPA ile mezun oldum. Veri yapıları, algoritma analizi, yazılım mimarisi ve web teknolojileri üzerine kapsamlı eğitim aldım. Bitirme projemde IoT cihazları için gerçek zamanlı veri analizi platformu geliştirdim.'
          },
          {
            school: 'Warsaw University of Technology',
            date: '2021 - 2022',
            degree: 'Elektronik ve Bilgi Teknolojileri (Erasmus Programı)',
            description: 'Erasmus programı kapsamında bir dönem boyunca gömülü sistemler, IoT ve ağ teknolojileri üzerine çalışmalar yaptım. Uluslararası bir ekiple IoT tabanlı akıllı ev sistemi projesi geliştirdim.'
          }
        ]
      },
      experience: {
        title: 'İş Deneyimi',
        items: [
          {
            company: 'Simply Deliver',
            date: '2022 - Devam Ediyor',
            position: 'Back End Developer',
            responsibilities: [
              'Mikroservis mimarisi ile ölçeklenebilir API\'lar geliştirme',
              'Oracle veritabanı optimizasyonu ve performans iyileştirmeleri',
              'CI/CD süreçlerinin iyileştirilmesi ve test otomasyonu',
              'Agile/Scrum metodolojisi ile proje yönetimi'
            ]
          },
          {
            company: 'Eralp Software',
            date: '2022',
            position: 'Part-Time Front End Developer',
            responsibilities: [
              'React ve TypeScript ile modern web uygulamaları geliştirme',
              'Responsive tasarım ve kullanıcı deneyimi optimizasyonu',
              'REST API entegrasyonları ve state management'
            ]
          },
          {
            company: 'Dijisan',
            date: '2021',
            position: 'Stajyer',
            responsibilities: [
              'Web uygulamaları için UI/UX tasarımları',
              'HTML, CSS ve JavaScript ile frontend geliştirme',
              'Temel seviye backend entegrasyonları'
            ]
          }
        ]
      }
    },
    en: {
      education: {
        title: 'Education',
        items: [
          {
            school: 'Yasar University',
            date: '2017 - 2022',
            degree: 'Software Engineering',
            description: 'Graduated with a 3.57 GPA. Received comprehensive training in data structures, algorithm analysis, software architecture, and web technologies. Developed a real-time data analysis platform for IoT devices as my graduation project.'
          },
          {
            school: 'Warsaw University of Technology',
            date: '2021 - 2022',
            degree: 'Electronics and Information Technology (Erasmus Program)',
            description: 'Studied embedded systems, IoT, and network technologies during one semester through the Erasmus program. Developed an IoT-based smart home system project with an international team.'
          }
        ]
      },
      experience: {
        title: 'Work Experience',
        items: [
          {
            company: 'Simply Deliver',
            date: '2022 - Present',
            position: 'Back End Developer',
            responsibilities: [
              'Developing scalable APIs with microservice architecture',
              'Oracle database optimization and performance improvements',
              'Improving CI/CD processes and test automation',
              'Project management with Agile/Scrum methodology'
            ]
          },
          {
            company: 'Eralp Software',
            date: '2022',
            position: 'Part-Time Front End Developer',
            responsibilities: [
              'Developing modern web applications with React and TypeScript',
              'Responsive design and user experience optimization',
              'REST API integrations and state management'
            ]
          },
          {
            company: 'Dijisan',
            date: '2021',
            position: 'Intern',
            responsibilities: [
              'UI/UX designs for web applications',
              'Frontend development with HTML, CSS, and JavaScript',
              'Basic backend integrations'
            ]
          }
        ]
      }
    }
  };

  const currentContent = content[language];

  return (
    <>
      <section id="egitim" className="section">
        <h2>{currentContent.education.title}</h2>
        {currentContent.education.items.map((item, index) => (
          <div key={index} className="education-item">
            <h3>{item.school}</h3>
            <p className="date">{item.date}</p>
            <p>{item.degree}</p>
            <p>{item.description}</p>
          </div>
        ))}
      </section>

      <section id="deneyim" className="section">
        <h2>{currentContent.experience.title}</h2>
        {currentContent.experience.items.map((item, index) => (
          <div key={index} className="experience-item">
            <h3>{item.company}</h3>
            <p className="date">{item.date}</p>
            <p>{item.position}</p>
            <ul>
              {item.responsibilities.map((resp, idx) => (
                <li key={idx}>{resp}</li>
              ))}
            </ul>
          </div>
        ))}
      </section>
    </>
  );
};

export default Experience;
